mapper.select:
select DISTINCT tp.*,t.id as tid, t.name as tname,c.id as cid,c.name as cname,s.id as sid,s.name as sname,m.id as mid,m.name as mname FROM test_paper tp,teacher t,school s,course c,major m where tp.postTeacherId=t.id and tp.schoolId =s.id and tp.courseId=c.id and tp.majorId=m.id
and tp.name like concat('%',#{tp.name},'%')
and tp.courseId=#{tp.courseId}
and tp.postTeacherId =#{tp.postTeacherId}
limit #{page},#{size}

select count(*) from test_paper tp


    and tp.courseId=#{tp.courseId}


    and tp.postTeacherId =#{tp.postTeacherId}
