mapper.select:
    select qa.*,t.id as tid,t.name as tname from qa_question qa ,teacher t
where qa.teacherId=t.id


      and qa.courseId=#{qa.courseId}
      and qa.chapterId =#{qa.chapterId}


      and qa.courseId =#{qa.courseId}


      and qa.dot =#{qa.dot}


      limit #{page},#{size}

select count(*) from qa_question qa



    and qa.courseId=#{qa.courseId}
    and qa.chapterId =#{qa.chapterId}


    and qa.courseId =#{qa.courseId}


    and qa.dot =#{qa.dot}
